const express = require('express');
const app = express();
const path = require('path');

// Middleware to check if the request is within working hours
const checkWorkingHours = (req, res, next) => {
  const date = new Date();
  const day = date.getDay();
  const hour = date.getHours();

  if (day >= 1 && day <= 5 && hour >= 9 && hour < 17) {
    next(); // Continue to the requested route
  } else {
    res.send('This web application is only available during working hours (Monday to Friday, from 9 AM to 5 PM).');
  }
};

// Use the middleware for all routes
app.use(checkWorkingHours);

// Set the view engine to Pug
app.set('view engine', 'pug');
app.set('views', path.join(__dirname, 'views'));

// Serve static files (CSS, images)
app.use(express.static(path.join(__dirname, 'public')));

// Routes
app.get('/', (req, res) => {
  res.render('home'); // Render the 'home' Pug view
});

app.get('/services', (req, res) => {
  res.render('services'); // Render the 'services' Pug view
});

app.get('/contact', (req, res) => {
  res.render('contact'); // Render the 'contact' Pug view
});

// Start the server
const PORT = process.env.PORT || 3000;
app.listen(PORT, () => {
  console.log(`Server is running on http://localhost:3000`);
});
